import React, {Component} from 'react';
import debounce from 'lodash/debounce';
import axios from 'axios';
// import logo from './logo.svg';
import './App.css';
import Suggestion from './Suggestion';

class TwitterTextArea extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentInput: "",
      currentSuggestions: [],
      charactersLeft: 280
    };

    this.handleChange = this.handleChange.bind(this);
    this.requestSuggestions = this.requestSuggestions.bind(this);
    this.useSuggestion = this.useSuggestion.bind(this);
  }
  handleChange = debounce( (e) => {
    var currentText = e.target.value; // this is the entire value of textarea
    this.setState({
      currentInput: currentText,
      charactersLeft: 280 - currentText.length
    });
    var currentTextArray = currentText.split(" "); // going to split(" ") by each word

    //now looking for any possible tagging and then making possible changes
    for (var i = 0; i < currentTextArray.length; i++) {
      if (/[a-zA-Z0-9]/.test(currentTextArray[i].charAt(1)) && /^(@)/.test(currentTextArray[i]) && currentTextArray[i].length >= 3) {
        // if first character is @ and second character is letter or number
        //continue search
        console.warn("currentTextArray[i]:", currentTextArray[i]);
        console.log("first character is @. second character is a letter or number. more than one @12. so we're going to run the API search");
        let noAtSymbol = currentTextArray[i].slice(1); // just getting rid of the @ sign for search
        this.requestSuggestions(noAtSymbol); // SEARCHING
      }
    }
    // console.log(currentText);
  }, 1000)
  // handleChange(e) {
  //   var currentText = e.target.value; // this is the entire value of textarea
  //   this.setState({
  //     currentInput: currentText,
  //     charactersLeft: 280 - currentText.length
  //   });
  //   var currentTextArray = currentText.split(" "); // going to split(" ") by each word
  //
  //   //now looking for any possible tagging and then making possible changes
  //   for (var i = 0; i < currentTextArray.length; i++) {
  //     if (/[a-zA-Z0-9]/.test(currentTextArray[i].charAt(1)) && /^(@)/.test(currentTextArray[i]) && currentTextArray[i].length >= 3) {
  //       // if first character is @ and second character is letter or number
  //       //continue search
  //       console.warn("currentTextArray[i]:", currentTextArray[i]);
  //       console.log("first character is @. second character is a letter or number. more than one @12. so we're going to run the API search");
  //       let noAtSymbol = currentTextArray[i].slice(1); // just getting rid of the @ sign for search
  //       this.requestSuggestions(noAtSymbol); // SEARCHING
  //     }
  //   }
  //   // console.log(currentText);
  // }
  requestSuggestions(typed) {
    axios.get("http://localhost:4000/twitter/user/search?username=" + typed).then(res => {
      // console.log(res.data.users);
      const allUsers = res.data.users;
      const sixSuggestions = allUsers.slice(0, 6);
      console.log(sixSuggestions);
      const FinalSuggestions = [];

      // looping through the 6 item array to get the info we want
      for (let i = 0; i < sixSuggestions.length; i++) {
        // console.log(sixSuggestions[i].screen_name);
        let screen_name = sixSuggestions[i].screen_name;
        // console.log(sixSuggestions[i].profile_image_url);
        let profile_image_url = sixSuggestions[i].profile_image_url;
        FinalSuggestions.push({screen_name: screen_name, profile_image_url: profile_image_url});

      }
      this.setState({currentSuggestions: FinalSuggestions})
      console.log("=====");
      console.log(this.state);
    })
  }
  useSuggestion(name) {
    console.log("selected name", name);

  }
  render() {
    const displaySuggestions = this.state.currentSuggestions.map((user, index) => <Suggestion screen_name={user.screen_name} src={this.state.currentSuggestions[index].profile_image_url} onClick={this.useSuggestion} key={index}/>)
    return (<div id="tweetZone">

      <textarea type="text" id="tweetBox" onChange={this.handleChange} autoFocus="autoFocus" maxLength="280"/>
      <p>{this.state.charactersLeft}</p>
      <ul>{displaySuggestions}</ul>
    </div>);
  }
}

export default TwitterTextArea;
